import { OddCard } from "./OddCard"
import '../../css/items/GameCard.scss'
import { useEffect, useState } from "react"

export const GameCard = ({game, onOddClick}) => {
    const [ oddsListKey, setOddsListKey ] = useState(null)
    const [ oddsList, setOddsList ] = useState([])
    const [ isSelected, setIsSelected ] = useState(false)

    useEffect(() => {
        const newOddsListKey = game.bookmakers[0].markets[0].key
        const odds = game.bookmakers[0].markets[0].outcomes
        const newOddsList = {}
        odds.forEach(odd => {
            console.log(odd)
            newOddsList[newOddsListKey+odd.name] = {
                id: newOddsListKey+odd.name,
                name: odd.name,
                value: odd.price,
                selected: false
            }
        })

        setOddsListKey(newOddsListKey)
        setOddsList(newOddsList)
    }, [])

    const handleOnOddClick = (odd) => {
        onOddClick()
        const updatedOddsList = oddsList
        updatedOddsList[odd.id].selected = !updatedOddsList[odd.id].selected
        setIsSelected(!isSelected)
    }

    return (
        <div className='game-card'>
            <div className='id-labels'>
                <label className='main-label'>
                    { game.homeTeam } - { game.awayTeam }
                </label>
                <label className='secondary-label'>
                    { game.commenceTime }
                </label>
            </div>

            <div className='odds-list'>
                { (oddsList && oddsListKey) && Object.values(oddsList).map(odd => 
                    <OddCard key={`${oddsListKey}_${odd.name}`} odd={odd} onClick={() => handleOnOddClick(odd)} />
                ) }

            </div>
        </div>
    )
}
