import { useState, useEffect } from 'react'
import { getGames } from '../../utils/api.js'

function GamesList() {
  const [ gamesList, setGamesList ] = useState([])

  const fetchGamesList = async () => {
    const newGamesList = await getGames();
    setGamesList(newGamesList)
    console.log("=>", newGamesList)
  }

  useEffect(() => {
    fetchGamesList()
  }, [])

  return (
    <div className="GamesList">
      <div className='gameslist-container'>
        <div className='searchbar-container'>
          <input type="text"/>
        </div>
        <div className='gameslist'>
          {
            gamesList.map(game => 
              <div className='game-card'>
                <div className='labels'>
                  <label className='main-label'>
                    {game.homeTeam} vs {game.awayTeam}
                  </label>
                  <label className='secondary-label'>
                    {game.commenceTime}
                  </label>
                </div>

                <div className='odds-list'>

                  <div className='odd-card'>

                  </div>
                </div>
              </div>
            )
          }
        </div>

      </div>

      <div className='betlist-container'>

      </div>
    </div>
  );
}

export default GamesList;
